package com.v2ray.ang.pluginimport android.content.Intentimport android.content.pm.PackageManagerimport com.v2ray.ang.AngApplicationclass PluginList : ArrayList<Plugin>() {    init {        addAll(            AngApplication.application.packageManager.queryIntentContentProviders(                Intent(PluginContract.ACTION_NATIVE_PLUGIN), PackageManager.GET_META_DATA            )                .filter { it.providerInfo.exported }.map { NativePlugin(it) })    }    val lookup = mutableMapOf<String, Plugin>().apply {        for (plugin in this@PluginList.toList()) {            fun check(old: Plugin?) {                if (old != null && old != plugin) {                    this@PluginList.remove(old)                }            }            check(put(plugin.id, plugin))        }    }}